    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
     Curso cursoSeleccionado = null;
        
        for(Curso u : LoginA.cursos){
            if(u.Nombre.equals(jComboBox1.getSelectedItem().toString())){
                cursoSeleccionado = u;
                break;
            }
        }
        
        if(cursoSeleccionado!=null){
            
        DefaultTableModel data = new DefaultTableModel(new String[]{"Nombre","Apellido","Usario","Password","Carnet"},cursoSeleccionado.alumnos.size());
        jTable1.setModel(data);
        
        int row=0;
        
        for(Alumno e : cursoSeleccionado.alumnos){
            jTable1.setValueAt(e.nombre, row, 0);
            jTable1.setValueAt(e.apellido, row, 1);  
            jTable1.setValueAt(e.usuario, row, 2); 
            jTable1.setValueAt(e.password, row, 3);
            jTable1.setValueAt(e.carnet, row, 4);  
            row++;
        }
            
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

