    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
       Curso CursoSeleccionado = null;

        for (Curso c : LoginA.cursos) {
            if (c.alumnos.equals(jComboBox1.getSelectedItem().toString())) {
                CursoSeleccionado = c;
                break;
            }
        }
        JFrame frame = new JFrame("");
        String m = JOptionPane.showInputDialog(frame, "Agregar Nota", 0);
        if (m != null) {
            if (Integer.parseInt(m) > 0) {
                int row = jTable1.getSelectedRow();
                String value = jTable1.getModel().getValueAt(row, 2).toString();
                for (Alumno e : CursoSeleccionado.alumnos) {
                    if (e.carnet.equals(value)) {
                        e.nota = Integer.parseInt(m);
                    }
                }

            }
        }
    }//GEN-LAST:event_jButton3ActionPerformed

